import { takeLatest, put, call, all } from 'redux-saga/effects'
import UserActionTypes from './userActionTypes'
import { auth, googleProvider, createUserProfileDocument } from '../../utils/firebase.utils'
import { googleSignInSuccess, googleSigninFailure } from './userAction'

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider)
    const userRef = yield createUserProfileDocument(user)
    const userSnapshot = yield userRef.get()
    yield put(googleSignInSuccess({
      id: userSnapshot.id, ...userSnapshot.data()
    }))
  } catch (e) {
    yield put(googleSigninFailure(e))
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGNIN_START, onGoogleSignInStart)
}

export function* userSaga() {
  yield all([call(onGoogleSignInStart)])
}
