import React from 'react'
import { Route } from 'react-router-dom'
import { connect } from 'react-redux'
import { createStructuredSelector } from 'reselect'
import CollectionOverview from '../../components/collectionoverview/CollectionOverview'
import CollectionPage from '../../pages/CollectionPage/CollectionPage'
import { fetchCollectionsStartAsync } from '../../redux/shop/shopAction/fetchCollectionsStartAsync'
import { selectCollectionFetching } from '../../redux/shop/shopSelector/selectCollectionFetching'
import WithLoader from '../../components/loader/Loader'

import './ShopPage.scss'

class ShopPage extends React.Component {

  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props
    fetchCollectionsStartAsync()
  }

  render() {
    const { match, isCollectionFetching } = this.props
    return (
      <div className="shop-page">
        <Route exact path={`${match.path}`} render={(props) => (
          <CollectionOverviewLoader isLoading={isCollectionFetching} {...props} />
        )} />
        <Route path={`${match.path}/:collectionId`} render={(props) => (
          <CollectionPageLoader isLoading={isCollectionFetching} {...props} />
        )} />
      </div>
    )
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectCollectionFetching
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
