import React, { Component } from 'react'
import { auth } from '../../utils/firebase.utils'
import FormInput from '../forminput/FormInput'
import CostumButton from '../costumButton/CostumButton'

import './SignUp.scss'

class SignUp extends Component {
  state = {
    displayName: '',
    email: '',
    password: '',
    confirmPassword: '',
  }

  handleSubmit = async event => {
    event.preventDefault()

    const { displayName, email, password, confirmPassword } = this.state

    if (password !== confirmPassword) {
      alert("Password don't match")
      return
    }

    try {
      const { user } = await auth.createUserWithEmailAndPassword(password, confirmPassword)
      createUserProfileDoc(user, { displayName })

    } catch (err) {
      console.log(err.message)
    }
  }

  render() {
    const { displayName, email, password, confirmPassword } = this.state

    return (
      <div className="sign-up">
        <h1 className="title">Do not have an account</h1>
        <span>Sign up with email and password</span>

        <form className="sign-up-form" onSubmit={this.handleSubmit}>
          <FormInput
            type="text"
            name="displayName"
            value={displayName}
            onChange={this.handleChange}
            label="Display Name"
            required
          />

          <FormInput
            type="email"
            name="email"
            value={email}
            onChange={this.handleChange}
            label="Email"
            required
          />

          <FormInput
            type="password"
            name="password"
            value={password}
            onChange={this.handleChange}
            label="Password"
            required
          />

          <FormInput
            type="password"
            name="confirmPassword"
            value={confirmPassword}
            onChange={this.handleChange}
            label="Confirm password"
            required
          />
          <CostumButton type='submit'>Sign Up</CostumButton>
        </form>
      </div>
    )
  }
}
export default SignUp
