//     selectcollections,
//     collections =>
//       collections.find(
//         collection =>
//           collection.id === COLLECTION_ID_MAP[collectionUrlParams]
//       )
//   )
// }

export const selectCollections = memoize((collectionUrlParams) = createSelector(
  selectcollections,
  collections => collections[collectionUrlParams]
))
