import { CartActionTypes } from './cartActionTypes'

const cartToggleHidden = () => ({
  type: CartActionTypes.CURT_TOGGLE_HIDDEN
})

export const addItem = item => ({
  type: CartActionTypes.ADD_ITEM,
  peyload: item
})

export default cartToggleHidden
